#include <iostream>
#include <sstream>
#include <string>
using namespace std;

int main() {
    string line;
    getline(cin, line);
    if (!line.empty()) {
        istringstream iss(line);
        int a, b;
        iss >> a >> b;
        cout << (a + b) << endl;
    }
    return 0;
}

referenceSolutions.go:
package main

import (
    "bufio"
    "fmt"
    "os"
    "strconv"
    "strings"
)

func main() {
    scanner := bufio.NewScanner(os.Stdin)
    if scanner.Scan() {
        line := scanner.Text()
        if line != "" {
            parts := strings.Fields(line)
            if len(parts) >= 2 {
                a, _ := strconv.Atoi(parts[0])
                b, _ := strconv.Atoi(parts[1])
                fmt.Println(a + b)
            }
        }
    }
}

